 local BlasRadius = 5 for i,v in pairs(game.Workspace:GetChildren()) do if v.className == "Part" then local ea = Instance.new("Explosion", v) ea.BlastRadius = BlasRadius ea.Position = v.Position elseif v.className == "Model" then for i,v in pairs(v:GetChildren()) do if v.className == "Part" then local ea = Instance.new("Explosion", v) ea.BlastRadius = BlasRadius ea.Position = v.Position else end end end end